//# Parameter:
//#    options:
//#      the parameter provides ...
//#     defaults:
//#      the parameter provides ...
//# Comment:
//#    the method performs ...
//#
//#################################################################

LoadFile4DOM.prototype.set_defaults = function (options, defaults) {

  var obj = {};
  defaults = defaults || {};
  for (var d in defaults) {
    if (defaults.hasOwnProperty(d)) {
      obj[d] = defaults[d];
    }
  }
  for (var k in options) {
    if (options.hasOwnProperty(k)) {
      obj[k] = options[k];
    }
  }
  return obj;

};
